import { useEffect, useState } from 'react';
import { getPedidos, deletePedido } from '../services/pedidoService.ts';
import { Link, useNavigate } from 'react-router-dom';
import './css/ListaPedidos.css';

interface Pedido {
  id: string;
  nomeCliente: string;
  item: string;
  descricaoIten: string;
  dataCriacao: string;
}

export function ListaPedidos() {
  const [pedidos, setPedidos] = useState<Pedido[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    carregarPedidos();
  }, []);

  async function carregarPedidos() {
    const resposta = await getPedidos();
    console.log('üì¶ Pedido:', JSON.stringify(resposta, null, 2));
    setPedidos(resposta);
  }

  async function excluir(id: string) {
    if (window.confirm('Tem certeza que deseja excluir este pedido?')) {
      await deletePedido(id);
      carregarPedidos();
    }
  }

  return (
    <div className="container">
      <h1 className="title">üìã Lista de Pedidos</h1>

      <div className="top-actions">
        <Link to="/novo" className="novo-link">
          ‚ûï Novo Pedido
        </Link>
      </div>

      {pedidos.length === 0 ? (
        <p className="nenhum">Nenhum pedido cadastrado.</p>
      ) : (
        <ul className="pedido-list">
          {pedidos.map(pedido => (
            <li key={pedido.id} className="pedido-item">
              <div className="pedido-dados">
                <h3>üë§ Cliente: {pedido.nomeCliente}</h3>
                <p>üì¶ Produto: {pedido.item}</p>
                <p>üìù Descri√ß√£o: {pedido.descricaoIten}</p>
                <p>üïí Criado em: {new Date(pedido.dataCriacao).toLocaleString()}</p>
              </div>
              <div className="acoes">
                <button
                  className="detalhes-btn"
                  onClick={() => navigate(`/pedidos/${pedido.id}`)}
                >
                  Detalhes
                </button>
                <button
                  className="excluir-btn"
                  onClick={() => excluir(pedido.id)}
                >
                  Excluir
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
