import { useEffect, useState } from 'react';
import { getPedidos, deletePedido } from '../services/pedidoService.ts';
import { Link } from 'react-router-dom';
import './ListaPedidos.css'; // IMPORTAÃ‡ÃƒO DO CSS

interface Pedido {
  id: string;
  nome: string;
}

export function ListaPedidos() {
  const [pedidos, setPedidos] = useState<Pedido[]>([]);

  useEffect(() => {
    carregarPedidos();
  }, []);

  async function carregarPedidos() {
    const resposta = await getPedidos();
    setPedidos(resposta);
  }

  async function excluir(id: string) {
    await deletePedido(id);
    carregarPedidos();
  }

  return (
    <div className="container">
      <h1 className="title">ðŸ“‹ Lista de Pedidos</h1>

      <div style={{ textAlign: 'right', marginBottom: '20px' }}>
        <Link to="/novo" className="novo-link">
          Novo Pedido
        </Link>
      </div>

      {pedidos.length === 0 ? (
        <p style={{ textAlign: 'center' }}>Nenhum pedido cadastrado.</p>
      ) : (
        <ul className="pedido-list">
          {pedidos.map(pedido => (
            <li key={pedido.id} className="pedido-item">
              <span>{pedido.nome}</span>
              <button onClick={() => excluir(pedido.id)} className="excluir-btn">
                Excluir
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
