import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getPedidoById } from '../services/pedidoService.ts';
import './css/DetalhesPedido.css';

interface Pedido {
  id: string;
  nomeCliente: string;
  item: string;
  descricaoItem: string;
  dataCriacao: string;
}

export function DetalhesPedido() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [pedido, setPedido] = useState<Pedido | null>(null);

  useEffect(() => {
    async function carregarDetalhes() {
      if (!id) return;
      const resposta = await getPedidoById(id);
      setPedido(resposta);
    }

    carregarDetalhes();
  }, [id]);

  if (!pedido) {
    return <p className="carregando">Carregando detalhes do pedido...</p>;
  }

  return (
    <div className="detalhes-container">
      <h2>ðŸ“„ Detalhes do Pedido</h2>

      <div className="detalhe">
        <strong>Cliente:</strong> <span>{pedido.nomeCliente}</span>
      </div>

      <div className="detalhe">
        <strong>Produto:</strong> <span>{pedido.item}</span>
      </div>

      <div className="detalhe">
        <strong>DescriÃ§Ã£o:</strong> <span>{pedido.descricaoItem || 'Sem descriÃ§Ã£o'}</span>
      </div>

      <div className="detalhe">
        <strong>Data de CriaÃ§Ã£o:</strong>{' '}
        <span>{new Date(pedido.dataCriacao).toLocaleString('pt-BR')}</span>
      </div>

      <button className="voltar-btn" onClick={() => navigate(-1)}>
        ðŸ”™ Voltar
      </button>
    </div>
  );
}
